package rs.saga.businessobject;public class SimpleOperationsService implements IOperationsService {    private IRequest request;    private IResponse response;    public SimpleOperationsService(IRequest request, IResponse response) {        this.request = request;        this.response = response;    }    @Override    public String createResponse() {        String responseMsg = "User " + response.getUser();        if (Math.random() < 0.5) {            responseMsg += " available during " + request.getStartAt() + " until " + request.getEndAt();        } else {            responseMsg += " not available during " + request.getStartAt() + " until " + request.getEndAt();        }        return responseMsg;    }}