package rs.saga.businessobject;import java.util.Date;public class SimpleOperationsService {    public String createResponse() {        Request request = new Request(new Date(2018, 11, 11), new Date(2018, 11, 20), "Fluffy");        Response response = new Response("John");        String responseMsg = "User " + response.getUser();        if (Math.random() < 0.5) {            responseMsg += " available during " + request.getStartAt() + " until " + request.getEndAt();        } else {            responseMsg += " not available during " + request.getStartAt() + " until " + request.getEndAt();        }        return responseMsg;    }}